INSERT [dbo].[TeacherBackground] ([Id], [TeacherId], [Title], [Description], [Type], [Status], [CanDelete], [CreatedBy], [CreatedDate], [ModifiedBy], [ModifiedDate]) VALUES (N'1', 1, N'BA of English - University of Pedagogy', N' <strong class="accent-color">
                                            English Linguistics
                                        </strong>
                                        is a university specializing in tertiary-level training of teachers and lecturers located in District 5. The university was founded as the Saigon National Pedagogical University in 1957 and reestablished by prime ministerial decree in 1976.
                                        In 1995, the university was incorporated in Ho Chi Minh City National University, but it was split to become an autonomous university with the current name in 1999.
                                        Between 1976 and 2006, the university educated 65,945 graduates, of which 1,000 were postgraduates. The university also offered training for 33,800 teachers and has education cooperations with over 50 universities worldwide.', N'EducationBackground', N'ACT', 0, N'admin', CAST(N'2017-03-10 16:25:00.000' AS DateTime), NULL, NULL)
INSERT [dbo].[TeacherBackground] ([Id], [TeacherId], [Title], [Description], [Type], [Status], [CanDelete], [CreatedBy], [CreatedDate], [ModifiedBy], [ModifiedDate]) VALUES (N'2', 1, N'BA of Oriental Studies – The Studies of Japan – University of Social Sciences and Humanities', N'<strong class="accent-color">
                                            Ho Chi Minh City University of Social Sciences and Humanities
                                        </strong>
                                        (Abbreviation: HCMUSSH, Vietnamese: Đại học Khoa học xã hội và Nhân văn Thành phố Hồ Chí Minh) is one of the member universities of Vietnam National University, Ho Chi Minh City, which includes the HCMUSSH, the University of Natural Sciences, the University of Technology, the International University, the University of Computer Science and the University of Economics and Law. The HCMUSSH plays an important role in Vietnam''s higher education, especially in the fields of social sciences and humanities.', N'EducationBackground', N'ACT', 0, N'admin', CAST(N'2017-03-10 16:25:00.000' AS DateTime), NULL, NULL)
INSERT [dbo].[TeacherBackground] ([Id], [TeacherId], [Title], [Description], [Type], [Status], [CanDelete], [CreatedBy], [CreatedDate], [ModifiedBy], [ModifiedDate]) VALUES (N'3', 1, N'MA of TESOL – Victoria University – Australia', N' <strong class="accent-color">
                                            Victoria University (VU)
                                        </strong>
                                        is a university based in Melbourne, Australia. It is a dual-sector tertiary institution providing courses in both higher education and Technical and Further Education (TAFE). 2016 marks VU''s centenary as an educational institution and its 25th anniversary as a university.
                                        VU is ranked in the world’s top 2% (THE World University Rankings 2016/2017) and firmly positioned in the QS World University Rankings 2016/2017.', N'EducationBackground', N'ACT', 0, N'admin', CAST(N'2017-03-10 16:25:00.000' AS DateTime), NULL, NULL)
INSERT [dbo].[TeacherBackground] ([Id], [TeacherId], [Title], [Description], [Type], [Status], [CanDelete], [CreatedBy], [CreatedDate], [ModifiedBy], [ModifiedDate]) VALUES (N'4', 1, N'PhD of English Language and Literature – Atlantic International University – USA', N'<strong class="accent-color">
                                            Atlantic International University
                                        </strong>
                                        is an American private non-profit distance education institution of higher and continuing education in Virginia Beach, Virginia. The university is associated with Edgar Cayce''s Association for Research and Enlightenment (A.R.E.) and its administrative offices are in the Don and Nancy de Laski Education Center on the main A.R.E. campus. The university is nationally accredited by the Distance Education Accrediting Commission (DEAC), which is a member of the Council for Higher Education Accreditation (CHEA), for its distance education and hybrid programs. The university also maintains licensure with the State Council of Higher Education for Virginia (SCHEV).', N'EducationBackground', N'ACT', 0, N'admin', CAST(N'2017-03-10 16:25:00.000' AS DateTime), NULL, NULL)
INSERT [dbo].[TeacherBackground] ([Id], [TeacherId], [Title], [Description], [Type], [Status], [CanDelete], [CreatedBy], [CreatedDate], [ModifiedBy], [ModifiedDate]) VALUES (N'5', 1, N'PhD of Comparative Linguistics – University of Social Sciences and Humanities
                                            Department of English Linguistics', N'<strong class="accent-color">
                                            English Linguistics
                                        </strong>
